import React, { useState, useEffect } from 'react'
import { ethers } from 'ethers';
import '../../node_modules/bootstrap/dist/css/bootstrap.min.css'
// 0xf078259544740e9CA50ed6d73763e02E9D809819
import './Login.css';
import { PingPongContractApi } from '../utils/PingPongportal.js'

function Login() {
    const [account, setAccount] = useState('login')
    const [message, setMessage] = useState('')
    const [recieved, setRecieved] = useState('')
    const [ack, setAck] = useState('')
    const [req, setReq] = useState(1)
    const [walletText, setWalletText] = useState("Connect Wallet")
    const [walletColor, setWalletColor] = useState("btn btn-danger")


    useEffect(async () => {
        const polygonMumbaiProvider = new ethers.providers.JsonRpcProvider("https://rpc.ankr.com/polygon_mumbai");
        const contractAddress = "0x9fF2c6D8bFf3b87538A156Ea1a768ec5A2d55B32";
        const contract = new ethers.Contract(
            contractAddress,
            PingPongContractApi,
            polygonMumbaiProvider
        )
        const data = await contract.currentRequestId();
        setReq(data)
    }, [])

    const provider = new ethers.providers.Web3Provider(window.ethereum);
    const polygonMumbaiProvider = new ethers.providers.JsonRpcProvider("https://rpc.ankr.com/polygon_mumbai");
    const avalancheFujiProvider = new ethers.providers.JsonRpcProvider("https://rpc.ankr.com/avalanche_fuji")
    console.log(polygonMumbaiProvider)


    const connectWallet = async () => {
        console.log('Requesting account...');
        // ❌ Check if Meta Mask Extension exists 
        if (window.ethereum) {
            console.log('detected');
            try {
                const accounts = await window.ethereum.request({
                    method: "eth_requestAccounts",
                });
                setAccount(accounts[0])
                setWalletText("Connected");
                setWalletColor("btn btn-success")
            } catch (error) {
                console.log('Error connecting...');
            }
        } else {
            alert('Meta Mask not detected');
        }
    }

    const handleSubmit = async () => {
        try {
            const contractAddress = "0x9fF2c6D8bFf3b87538A156Ea1a768ec5A2d55B32";
            const contract = new ethers.Contract(
                contractAddress,
                PingPongContractApi,
                polygonMumbaiProvider
            )
            console.log(contract)
            try {
                const data = await contract.ackFromDestination(req)
                setAck(data)
            }
            catch (err) {
                console.log("calling the function" + err)
            }
        }
        catch (err) {
            console.log("outside" + err)
        }
    }

    const sendPingFromSourceChain = async () => {
        const signer = provider.getSigner();
        const contractAddress = "0x9fF2c6D8bFf3b87538A156Ea1a768ec5A2d55B32";
        const contract = new ethers.Contract(
            contractAddress,
            PingPongContractApi,
            signer
        );
        const pingTransaction = await contract.pingDestination(0, "43113", 200000, 200000, "0xf7015AD80B60EA4A9e12d90ff00D68fAa8e08df4", message, 1000000000000);
        console.log("pingTransactionKey:", pingTransaction)
    }

    const receivePongToDestinationChain = async () => {
        const polygonMumbaiProvider = new ethers.providers.JsonRpcProvider("https://rpc.ankr.com/polygon_mumbai");
        const contractAddress1 = "0x9fF2c6D8bFf3b87538A156Ea1a768ec5A2d55B32";
        const contract1 = new ethers.Contract(
            contractAddress1,
            PingPongContractApi,
            polygonMumbaiProvider
        )
        const data = await contract1.currentRequestId();
        setReq(data)
        const signer = provider.getSigner()
        const contractAddress = "0xf7015AD80B60EA4A9e12d90ff00D68fAa8e08df4";
        const contract = new ethers.Contract(
            contractAddress,
            PingPongContractApi,
            avalancheFujiProvider
        );
        contract.pingFromSource(0, "80001", req).then((data) => {
            setRecieved(data)
        }).catch(() => {
            alert('connect to Fuji Network')
        })
    }

    return (

        <div>
            <br/>
            <button type="button" style={{ 'width': '10em', 'height': '3em', 'margin-left': '30em' }} class={walletColor} onClick={connectWallet}>
                {walletText}
            </button>
            <div class="main">
                <div class="card" style={{ 'width': '20em' }}>
                    <div class="card-header">
                        Mumbai Testnet
                    </div>
                    <div class="card-body">
                        <textarea class="form-control" id="textAreaExample2" rows="4" onChange={(e) => {
                            setMessage(e.target.value)
                        }} />
                        <br>
                        </br>
                        <button type="button" class="btn btn-success" onClick={sendPingFromSourceChain}>Send Message</button>
                        <br></br>
                        <br></br>
                        <textarea value={ack} class="form-control" id="textAreaExample2" rows="4" onChange={(e) => {
                            setMessage(e.target.value)
                        }} />
                        <br></br>
                        <button type="button" class="btn btn-success" onClick={handleSubmit}>Acknowledgement</button>
                    </div>
                </div>
                <div class="card" style={{ 'width': '20em' }}>
                    <div class="card-header">
                        Fuji Testnet
                    </div>
                    <div class="card-body">
                        <textarea value={recieved} class="form-control" id="textAreaExample2" rows="4" onChange={(e) => {setMessage(e.target.value)}} />
                        <br></br>
                        <button type="button" class="btn btn-success" onClick={receivePongToDestinationChain}>Message Recieved</button>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Login
