export const getMoralisLogo = (color: Color) => {
    switch (color) {
        case 'blue':
            return moralisLogoIconBlue();
        case 'black':
            return moralisLogoIconBlack();
        default:
        case 'white':
            return moralisLogoIconWhite();
    }
};

